<template>
  <view class="page"
    ><view class="card-container" v-if="!isSuccess">
      <view class="card">
        <view>提现金额</view>
        <view class="input-container">
          <view class="money">¥</view>
          <input
            placeholder=" "
            maxlength="8"
            type="digit"
            :value="inputVal"
            @input="inputChange"
          />
        </view>
        <view class="tips" v-if="!hasError">当前可提现余额{{ amount }}元</view>
        <view class="error-tips" v-if="hasError">输入金额超过可提现余额</view>
        <view :class="{btn: true, 'btn-disabled': !canSubmit}" @click="submit"
          >确认提现</view
        >
      </view>
      <view class="tips">
        <text>温馨提示：</text>
        <text>1.每次提现至少5元，最多49999元</text>
        <text>2.每月最多提现9.8万元</text>
        <text>3.每月最多提现10次</text>
        <text
          >4.请保证微信已完成实名，并且微信实名信息与小优天天赚中的实名信息一致，否则无法提现成功</text
        >
      </view>
    </view>
    <view class="view-btn" @click="viewList">查看提现记录>></view>
    <CustomerServiceBottom />
    <view class="success-container" v-if="isSuccess">
      <view class="icon-success"></view>
      <view class="text">提现申请成功</view>
      <view class="btn btn-back" @click="back">返回首页</view>
    </view></view
  >
</template>
<script>
import Vue from "vue";
import {Withdraw} from "@/utils/url";
import {mapState} from "vuex";
import CustomerServiceBottom from "../components/CustomerServiceBottom.vue";
import Api from "@/utils/api";
export default Vue.extend({
  data() {
    return {
      hasError: false,
      isSuccess: false,
      inputVal: "",
      amount: 0,
      canSubmit: false,
    };
  },
  components: {CustomerServiceBottom},
  onLoad() {
    // this.getWithdrawMethod();
  },
  mounted() {
    this.getMoney();
  },
  computed: {
    ...mapState("user", ["loginStatus", "userInfo"]),
    ...mapState("xiaoyou", ["xiaoyouInfo"]),
  },
  methods: {
    async getMoney() {
      const {data} = await Api.$getX(Withdraw.distributorMoney, {
        appId: this.userInfo.appId,
        disId: this.xiaoyouInfo?.id,
      });
      this.amount = data;
    },
    async getAuth(code) {
      const {data} = await Api.$getX(Withdraw.distributorAuth, {
        certificationType: code,
        disId: this.xiaoyouInfo?.id,
        appId: this.userInfo.appId,
      });
      if (!data) {
        uni.navigateTo({
          url: "/xiaoyouPages/withdraw/Auth",
        });
      }
    },
    // async getWithdrawMethod() {
    //   const {data} = await Api.$getX(Withdraw.withdrawPayAway, {
    //     appId: this.userInfo.appId,
    //   });
    //   const defaultType = data?.defaultType;
    //   // if (defaultType && defaultType?.code !== null) {
    //   //   this.getAuth(defaultType?.code);
    //   // }
    // },
    back() {
      uni.navigateTo({url: "/xiaoyouPages/home/index"});
    },

    money(val) {
      let num = val.toString();
      if (num.indexOf(".") == 0) {
        //第一位就是 .
        num = "0" + num;
      }
      num = num.replace(/[^\d.]/g, ""); //清除“数字”和“.”以外的字符
      num = num.replace(/\.{2,}/g, "."); //只保留第一个. 清除多余的
      num = num.replace(".", "$#$").replace(/\./g, "").replace("$#$", ".");
      num = num.replace(/^(\-)*(\d+)\.(\d\d).*$/, "$1$2.$3"); //只能输入两个小数
      if (num.indexOf(".") < 0 && num != "") {
        num = parseFloat(num);
      }
      return num;
    },
    inputChange(e) {
      let val = this.money(e.detail.value);
      if (val) {
        this.inputVal = val;

        if (e.detail.value > this.amount) {
          this.hasError = true;
        } else {
          this.hasError = false;
        }
        if (val == 0 || val.toString().lastIndexOf(".") === val.length - 1) {
          this.canSubmit = false;
        } else {
          this.canSubmit = true;
        }
      } else {
        this.canSubmit = false;
      }
    },

    async submit() {
      if (this.inputVal < 5) {
        return uni.showToast({
          title: "每次提现最少5元",
          icon: "none",
        });
      }
      if (this.inputVal > this.amount) {
        return uni.showToast({
          title: "超过了最大可提现金额",
          icon: "none",
        });
      }
      uni.showLoading();
      const {success} = await Api.$postX(Withdraw.withdrawMoney, {
        appId: this.userInfo.appId,
        withdraw: this.inputVal,
      });
      if (success) {
        this.isSuccess = true;
      }
      uni.hideLoading();
    },
    viewList() {
      // uni.navigateBack();
      uni.navigateTo({
        url: "/xiaoyouPages/withdraw/WithdrawList",
      });
    },
  },
});
</script>
